import headerLogo from "../images/header__logo.svg";

function Нeader({ title, email, isOpen, onMenuClick, onClose}) {
    return(
        <header className="header">
            <div className="header__logo-container">
                <img className="header__logo" src={headerLogo} alt="Место" />
                <button 
                    className="header__menu" 
                    type="button" 
                    onClick={isOpen ? onClose : onMenuClick} 
                    style={isOpen ? {backgroundImage: `url(/static/media/Close_Icon.a72cf4e2.svg)`} : { backgroundImage: `url(/static/media/menu_button.a46d5c1c.svg)` } } 
                />
            </div>
            <div className={`header__container ${isOpen ? "header__container_active" : ""}`}>
                <p className="header__user-email">{email}</p>
                <button type="button" className="header__button">{title}</button>
            </div>

        </header>
    );  
}

export default Нeader;
