import PopupWithForm from "./PopupWithForm";

function InfoTooltip({ isOpen, onClose, message }) {
    return(
        <PopupWithForm           
            isOpen={isOpen} 
            onClose={onClose} 
        >
            <img className="form__image" src={message.image} alt="Авторизация"></img>
            <p className="form__subtitle">{message.text}</p>

        </PopupWithForm>
    )
}
