function Main() {

    function handleEditAvatarClick() {
        document.querySelector(".profile__avatar").addEventListener("click", () => {
            document.querySelector(".popup_type_avatar").classList.add("popup_open");
        });
    }

    function handleEditProfileClick() {
        document.querySelector(".profile__edit-button").addEventListener("click", () => {
            document.querySelector(".popup_type_edit").classList.add("popup_open");
        });
    }

    function handleAddPlaceClick() {
        document.querySelector(".profile__add-button").addEventListener("click", () => {
            document.querySelector(".popup_type_new-card").classList.add("popup_open");
        });
    }

    return(
        <main>
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" onClick={handleEditAvatarClick} src="#" alt="Фото профиля" />
                </div>
                <div className="profile__info"> 
                    <div className="profile__container">
                        <h1 className="profile__name">Жак-Ив Кусто</h1>
                        <button onClick={handleEditProfileClick} type="submit" className="profile__edit-button"></button>
                    </div>
                    <p className="profile__career">Исследователь океана</p>
                </div>
                <button onClick={handleAddPlaceClick} type="button" className="profile__add-button"></button>
            </section>

            <section className="grid-galery"></section>
        </main>
    );
}

export default Main;
