import { useRef, useEffect } from "react";
import PopupWithForm from "./PopupWithForm.js";

export default function DeleteCardPopup({ isOpen, onClose, onCloseOverlayClick }) {

    function handleCardDelete(evt) {
        evt.preventDefault();
        api.handlerdeleteCards(selectedCardDelet._id)
        .then(() => {
            setCards(cards => cards.filter((c) => c._id !== selectedCardDelet._id));
            setIsDeletePopupOpen(false);
        })
        .catch((err) => {
            console.log(err);
        });
    }

    return(
        <PopupWithForm 
            name="delete-image" 
            title="Вы уверены?" 
            buttonText="Да" 
            isOpen={isOpen} 
            onClose={onClose} 
            onSubmit={handleSubmit} 
            onCloseOverlayClick={onCloseOverlayClick}
        />
    )
}
