import { useState, useContext, useEffect} from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import PopupWithForm from "./PopupWithForm.js";

export default function EditProfilePopup({ isOpen, onClose, onUpdateUser}) {

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");

    // const [nameDirty, setNameDirty] = useState(false);
    // const [descriptionDirty, setDescriptionDirty] = useState(false);

    // const [nameError, setNameError] = useState("Вы пропустили это поле");
    // const [descriptionError, setDescriptionError] = useState("Вы пропустили это поле");

    const currentUser = useContext(CurrentUserContext);

    function handleNameChange(evt) {
      setName(evt.target.value);
      // if(evt.target.value.length <= 1 || evt.target.value.length >= 40) {
      //   setNameError("Текст должен быть не короче 2 символов");
      // } else {
      //   setNameError("");
      // }
    }

    function handleDescriptionChange(evt) {
      setDescription(evt.target.value);
      // if(evt.target.value.length <= 1 || evt.target.value.length >= 200) {
      //   setDescriptionError("Текст должен быть не короче 2 символов");
      // } else {
      //   setDescriptionError("");
      // }
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);


    function handleSubmit(evt) {
      evt.preventDefault();

      onUpdateUser({
        name: name,
        about: description,
      });
    }

    // const blurHandler = (evt) => {
    //   switch(evt.target.name) {
    //     case "name":
    //       setNameDirty(true);
    //       break;
    //     case "description": 
    //       setDescriptionDirty(true);
    //       break;
    //     default:
    //       break;
    //   }
    // }

    return(
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <input 
              className="form__user-info form__user-info_user_name form__input" 
              id="input-name" name="name" value={name || ""} 
              onChange={handleNameChange} 
              type="text" 
              placeholder="Имя" 
              minLength="2" 
              maxLength="40"
              // onBlur={evt => blurHandler(evt)}
            />
            <div className="form__input-error">
              {/* {(nameDirty && nameError) && <span className="form__error_visible input-name-error">{nameError}</span>} */}
            </div>

            <input 
              className="form__user-info form__user-info_user_job form__input" 
              id="input-job" 
              name="description" 
              value={description || ""} 
              onChange={handleDescriptionChange} 
              type="text" 
              placeholder="О себе" 
              minLength="2" 
              maxLength="200"
              // onBlur={evt => blurHandler(evt)}
            />
            <div className="form__input-error">
              {/* {(descriptionDirty && descriptionError) && <span className="form__error_visible input-name-error">{nameError}</span>} */}
            </div>
        </PopupWithForm>
    );
}
