import { useState, useContext, useEffect} from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import PopupWithForm from "./PopupWithForm.js";

export default function EditProfilePopup({ isOpen, onClose, onUpdateUser}) {

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [hideNameError, setHideNameError] = useState(false);
    const [hideDescriptionError, setHideDescriptionError] = useState(false);
    const [showNameError, setShowNameError] = useState("Вы пропустили это поле");
    const [showDescriptionError, setShowDescriptionError] = useState("Вы пропустили это поле");


    const currentUser = useContext(CurrentUserContext);

    function handleNameChange(evt) {
      setName(evt.target.value);
    }

    function handleDescriptionChange(evt) {
      setDescription(evt.target.value);
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);


    function handleSubmit(evt) {
      evt.preventDefault();

      onUpdateUser({
        name: name,
        about: description,
      });
    }

    function blurHandler(evt) {
      switch (evt.target.name) {
        case "name":
          setHideNameError(true);
          break;
        case "description":
          setHideDescriptionError(true);
          break;
        default: 
        break;
      }
    }

    return(
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <input onBlur={blurHandler} className="form__user-info form__user-info_user_name form__input" id="input-name" name="name" value={name || ""} onChange={handleNameChange} type="text" required placeholder="Имя" minLength="2" maxLength="40" novalidate/>
            {(hideNameError && showNameError) && <span className="form__input-error input-name-error">{showNameError}</span>}
            <input onBlur={blurHandler} className="form__user-info form__user-info_user_job form__input" id="input-job" name="description" value={description || ""} onChange={handleDescriptionChange} type="text" required placeholder="О себе" minLength="2" maxLength="200" novalidate/>
            {(hideDescriptionError && showDescriptionError) && <span className="form__input-error input-job-error">{showDescriptionError}</span>}
        </PopupWithForm>
    );
}
