import { useRef, useEffect } from "react";
import PopupWithForm from "./PopupWithForm.js";

export default function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar, onCloseOverlayClick, onCloseKeyDown }) {

    const popupLinkRef = useRef();

    useEffect(() => {
        const currentPopup = popupLinkRef.current;

        currentPopup.addEventListener("click", onCloseOverlayClick);
        document.addEventListener("keydown", onCloseKeyDown);
        return() => {
            document.removeEventListener("keydown", onCloseKeyDown);
            currentPopup.removeEventListener("click", onCloseOverlayClick);
        }
    }, [isOpen, onClose]);

    function handleSubmit(evt) {
        evt.preventDefault();

        onUpdateAvatar({
          link: popupLinkRef.current.value
        });
    }

    useEffect(() => {
        popupLinkRef.current.value = "";
    }, [isOpen]);

    return(
        <PopupWithForm name="avatar" title="Обновить аватар" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit} ref={popupLinkRef} >
            <input className="form__user-info form__user-info_photo_src" id="input-avatar" name="link" type="url" ref={popupLinkRef} required placeholder="Ссылка на картинку" />
            <span className="form__input-error input-avatar-error"></span>
        </PopupWithForm>
    )
}
