import { useRef, useState } from "react";
import PopupWithForm from "./PopupWithForm.js";

export default function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar }) {

    const [value, setValue] = useState('')

    const avatarRef = useRef();

    function handleSubmit(evt) {
        evt.preventDefault();

        onUpdateAvatar(
            setValue(evt.target.value));
      }

    return(
        <PopupWithForm name="avatar" title="Обновить аватар" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <input className="form__user-info form__user-info_photo_src" id="input-avatar" name="link" type="url" value={value} required placeholder="Ссылка на картинку">
                {avatarRef.current}
            </input>    
            <span className="form__input-error input-avatar-error"></span>
        </PopupWithForm>
    )
}
