import {useEffect, useState} from 'react';
import api from "../utils/api";

function Main(props) {

  const [userName, setUserName] = useState("Жак-Ив Кусто");
  const [userDescription, setUserDescription] = useState("Исследователь океана");
  const [userAvatar, setUserAvatar] = useState();

  useEffect(() => {
    api.getUserInfoFromServer(userAvatar, userName, userDescription)
    .then(res => {
      const userData = {
          avatar: res.avatar,
          name: res.name,
          about: res.about
      };
      setUserAvatar(userData.avatar);
      setUserName(userData.name);
      setUserDescription(userData.about);
    })
    .catch((err) => {
        console.log(err);
    });
  });

  const [cards, setCards] = useState([]);

  useEffect(() => {
      api.getInitialCards(cards)
      .then(res => {
          const arr = res.results.map((item) => {
              return {
                  src: item.link,
                  alt: item.name,
                  title: item.name,
                  like: item.like.length,
                  id: item._id
              }
         })
         console.log(arr);
        //   setCards(arr);
      })
      .catch((err) => {
          console.log(err);
      });
  }, [cards])

    return(
        <main>
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" onClick={props.onEditAvatar} src={userAvatar} alt="Фото профиля" />
                </div>
                <div className="profile__info"> 
                    <div className="profile__container">
                        <h1 className="profile__name">{userName}</h1>
                        <button onClick={props.onEditProfile} type="submit" className="profile__edit-button"></button>
                    </div>
                    <p className="profile__career">{userDescription}</p>
                </div>
                <button onClick={props.onAddPlace} type="button" className="profile__add-button"></button>
            </section>

            <section className="grid-galery">
                {cards.map((card) => {
                    return (
                        <div className="card" key={card.id}>
                            <img className="card__image" alt={card.alt} src={card.src} />
                            <button type="button" className="card__remove-button"></button>
                            <div className="card__container">
                                <h2 className="card__title">{card.title}</h2>
                                <div className="card__like-container">
                                    <button type="button" className="card__button"></button>
                                    <p className="card__botton-counter">{card.like}</p>
                                </div>
                            </div>
                        </div>
                    );
                })}
            </section>
        </main> 
    );
}

export default Main;
