import {useEffect, useState, useContext} from 'react';
import api from "../utils/api";
import Card from "../components/Card.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";


function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {

  const currentUsereeee = useContext(CurrentUserContext);
  console.log(currentUsereeee);

  const [cards, setCards] = useState([]);

  useEffect(() => {
      api.getInitialCards(cards)
      .then(res => {
          const arr = res.map((item) => {
              return {
                  src: item.link,
                  alt: item.name,
                  title: item.name,
                  like: item.likes.length,
                  id: item._id,
                  ownerId: item.owner._id
              }
         })
          setCards(arr);
      })
      .catch((err) => {
          console.log(err);
      })
  }, []);

    return(
        <main>
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" onClick={onEditAvatar} src={currentUsereeee.avatar} alt="Фото профиля" />
                </div>
                <div className="profile__info"> 
                    <div className="profile__container">
                        <h1 className="profile__name">{currentUsereeee.name}</h1>
                        <button onClick={onEditProfile} type="submit" className="profile__edit-button"></button>
                    </div>
                    <p className="profile__career">{currentUsereeee.job}</p>
                </div>
                <button onClick={onAddPlace} type="button" className="profile__add-button"></button>
            </section>

            <section className="grid-galery">
                {cards.map((card) => {
                   return <Card key={card.id} card={card} onCardClick={onCardClick} />
                })}
            </section>
        </main> 
    );
}

export default Main;
