import {useEffect, useState, useContext} from 'react';
import api from "../utils/api";
import Card from "../components/Card.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";


function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {

  const currentUser = useContext(CurrentUserContext);

  const [cards, setCards] = useState([]);

  useEffect(() => {
      api.getInitialCards()
      .then(cardData => {
        setCards(cardData);
      })
      .catch((err) => {
          console.log(err);
      })
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((item) => {
        return item._id === currentUser._id
    });

    api.changeLikeCardStatus(card._id, isLiked)
    .then((newCard) => {
        const newCards = cards.map((c) => c._id === card._id ? newCard : c);
        setCards(newCards);
    })
    .catch((err) => {
        console.log(err);
    });
 }

 function handleCardDelete(card) {
     api.handleCardDelete()
 }

    return(
        <main>
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" onClick={onEditAvatar} src={currentUser.avatar} alt="Фото профиля" />
                </div>
                <div className="profile__info"> 
                    <div className="profile__container">
                        <h1 className="profile__name">{currentUser.name}</h1>
                        <button onClick={onEditProfile} type="submit" className="profile__edit-button"></button>
                    </div>
                    <p className="profile__career">{currentUser.about}</p>
                </div>
                <button onClick={onAddPlace} type="button" className="profile__add-button"></button>
            </section>

            <section className="grid-galery">
                {cards.map((card) => {
                   return <Card key={card._id} card={card} onCardClick={onCardClick} onCardLike={handleCardLike} onCardDelete={handleCardDelete} />
                })}
            </section>
        </main> 
    );
}

export default Main;
