import { useState, useContext, useEffect} from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import PopupWithForm from "./PopupWithForm.js";

export default function EditProfilePopup({ isOpen, onClose, onUpdateUser}) {

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [nameError, setNameErros] = useState(false);
    const [nameErroText, setNameErrosText] =useState("")

    const currentUser = useContext(CurrentUserContext);

    function handleNameChange(evt) {
      setName(evt.target.value);
      if(evt.target.name < 2) {
        setNameErros(true);
        setNameErrosText("Текст должен быть больше двух символов")
      }else {
        setNameErros(false);
        setNameErrosText("")
      }
    }

    function handleDescriptionChange(evt) {
      setDescription(evt.target.value);
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);


    function handleSubmit(evt) {
      evt.preventDefault();

      onUpdateUser({
        name: name,
        about: description,
      });
    }

    return(
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <input 
              className="form__user-info form__user-info_user_name form__input" 
              id="input-name" name="name" value={name || ""} 
              onChange={handleNameChange} 
              type="text" 
              placeholder="Имя" 
              minLength="2" 
              maxLength="40"
            />
            <span className={`form__input-error input-name-error ${nameError ? "form__error_visible" : ""}`}>{nameErroText}</span>
            <input 
              className="form__user-info form__user-info_user_job form__input" 
              id="input-job" 
              name="description" 
              value={description || ""} 
              onChange={handleDescriptionChange} 
              type="text" 
              placeholder="О себе" 
              minLength="2" 
              maxLength="200"
            />
            <span className="form__input-error input-name-error"></span>
        </PopupWithForm>
    );
}
