import {useContext} from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";

function Card({card, onCardClick}) {

    const currentUser = useContext(CurrentUserContext);
    console.log(card);

    // Определяем, являемся ли мы владельцем текущей карточки
    const isOwn = card.ownerId === currentUser._id;

    // Создаём переменную, которую после зададим в `className` для кнопки удаления
    const cardDeleteButtonClassName = (
    `${isOwn ? 'card__remove-button card__remove-button_visible' : 'card__remove-button'}`
    );

    // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
    const isLiked = card.likes.some((item) => {
        return item._id === currentUser._id
    });

    // Создаём переменную, которую после зададим в `className` для кнопки лайка
    const cardLikeButtonClassName = (
        `${isLiked ? 'card__button card__button_active' : 'card__button'}`
        );

    function handleClick() {
        onCardClick(card);
    }  

    return (
        <div className="card">
            <img className="card__image" alt={card.alt} src={card.src} onClick={handleClick} />
            <button type="button" className={cardDeleteButtonClassName}></button>
            <div className="card__container">
                <h2 className="card__title">{card.title}</h2>
                <div className="card__like-container">
                    <button type="button" className={cardLikeButtonClassName}></button>
                    <p className="card__botton-counter">{card.like}</p>
                </div>
            </div>
        </div>
    );
}

export default Card;
