import { useState, useContext, useEffect} from 'react';
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";
import PopupWithForm from "./PopupWithForm.js";
import { useForm } from "react-hook-form";

export default function EditProfilePopup({ isOpen, onClose, onUpdateUser}) {

  const { register, formState: { errors } } = useForm();

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");

    const currentUser = useContext(CurrentUserContext);

    function handleNameChange(evt) {
      setName(evt.target.value);
    }

    function handleDescriptionChange(evt) {
      setDescription(evt.target.value);
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);


    function handleSubmit(evt) {
      evt.preventDefault();

      onUpdateUser({
        name: name,
        about: description,
      });
    }

    return(
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <input 
              className="form__user-info form__user-info_user_name form__input" 
              id="name" 
              name="name" 
              value={name || ""} 
              onChange={handleNameChange} 
              type="text" 
              required 
              placeholder="Имя" 
              minLength="2" 
              maxLength="40"
              {...register('name', { required: true, minLength: 2 })}
            />
            <div className="form__input-error">
              {errors.name && errors.name.type === "required" && <span className="form__error_visible">Вы пропустили данное поле</span>}
              {errors.name && errors.name.type === "minLength" && <span className="form__error_visible">Текст должен былть не короче 2 символов</span>}
            </div>
            {/* <span className="form__input-error input-name-error"></span> */}
            <input className="form__user-info form__user-info_user_job form__input" id="input-job" name="description" value={description || ""} onChange={handleDescriptionChange} type="text" required placeholder="О себе" minLength="2" maxLength="200"/>
            <span className="form__input-error input-job-error"></span> 
        </PopupWithForm>
    );
}
