import { useState, useContext, useEffect} from 'react';
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import PopupWithForm from "./PopupWithForm.js";

export default function EditProfilePopup({ isOpen, onClose }) {

    const [name, setName] = useState("");
    const [description, setDescription] = useState("");

    const currentUser = useContext(CurrentUserContext);

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);

    function handleChange(evt) {
        evt.preventDefault();

        setName(evt.target.value);
        setDescription(evt.target.value);
      }

    return(
        <PopupWithForm name="edit" title="Редактировать профиль" buttonText="Сохранить" isOpen={isOpen} onClose={onClose}>
            <input className="form__user-info form__user-info_user_name form__input" id="input-name" name="name" value={name} onChange={handleChange} type="text" required placeholder="Имя" minLength="2" maxLength="40"/>
            <span className="form__input-error input-name-error"></span>
            <input className="form__user-info form__user-info_user_job form__input" id="input-job" name="description" value={description} onChange={handleChange} type="text" required placeholder="О себе" minLength="2" maxLength="200"/>
            <span className="form__input-error input-job-error"></span> 
        </PopupWithForm>
    );
}
